(* ::Subsubsection::Closed:: *)
(*p>0*)


{Sqrt[c - a*c*x]*Sqrt[1 - a^2*x^2]/x^2, x, 4, -((a*c*Sqrt[1 - a^2*x^2])/Sqrt[c - a*c*x]) - (c^2*(1 - a^2*x^2)^(3/2))/(x*(c - a*c*x)^(3/2)) + a*Sqrt[c]*ArcTanh[(Sqrt[c]*Sqrt[1 - a^2*x^2])/Sqrt[c - a*c*x]]}


(* ::Subsubsection::Closed:: *)
(*p<0*)


{Sqrt[c - a*c*x]/(x*Sqrt[1 - a^2*x^2]), x, 2, -2*Sqrt[c]*ArcTanh[(Sqrt[c]*Sqrt[1 - a^2*x^2])/Sqrt[c - a*c*x]]}


(* ::Subsection::Closed:: *)
(*Integrands of the form (g x)^(m/2) (d+e x)^(n/2) (d^2 - e^2 x^2)^(p/2)*)


(* Following pairs of integrands are equal. *)
{Sqrt[1 - a*x]/Sqrt[x], x, 3, Sqrt[x]*Sqrt[1 - a*x] + ArcSin[Sqrt[a]*Sqrt[x]]/Sqrt[a]}
{Sqrt[1 - a^2*x^2]/(Sqrt[x]*Sqrt[1 + a*x]), x, 4, Sqrt[x]*Sqrt[1 - a*x] + ArcSin[Sqrt[a]*Sqrt[x]]/Sqrt[a]}

