{Sqrt[1 - a^2*x^2]/(Sqrt[x]*Sqrt[1 - a*x]), x, 4, Sqrt[x]*Sqrt[1 + a*x] + ArcSinh[Sqrt[a]*Sqrt[x]]/Sqrt[a]}

{Sqrt[x]*Sqrt[1 - a*x], x, 4, -((Sqrt[x]*Sqrt[1 - a*x])/(4*a)) + (1/2)*x^(3/2)*Sqrt[1 - a*x] + ArcSin[Sqrt[a]*Sqrt[x]]/(4*a^(3/2))}
{Sqrt[x]*Sqrt[1 - a^2*x^2]/Sqrt[1 + a*x], x, 5, -((Sqrt[x]*Sqrt[1 - a*x])/(4*a)) + (1/2)*x^(3/2)*Sqrt[1 - a*x] + ArcSin[Sqrt[a]*Sqrt[x]]/(4*a^(3/2))}


